import React, { Component } from 'react'
import './UnPokemon.css'

class UnPokemon extends Component {

    state = {
        loading: true,
        pokemon: null
    }

    async componentDidMount() {
        const url = 'https://pokeapi.co/api/v2/pokemon/charizard'
        const response = await fetch(url)
        const data = await response.json()
        this.setState({ pokemon: data, loading: false })
        console.log(this.state.pokemon.sprites.back_default)
    }

    render() {
        return (
            <div>
                {this.state.loading ? (<div>loading...</div>) : (
                    <div Class='page'>
                        <div Class='title'>
                            {this.state.pokemon.name}
                        </div>
                        <div Class='body'>
                            <img Class='image' src={this.state.pokemon.sprites.front_default} />
                            <div>
                                <div>Type : {this.state.pokemon.types[0].type.name}, {this.state.pokemon.types[1].type.name}</div>
                            </div>
                            <div>Abilities : {this.state.pokemon.abilities[0].type.name}</div>
                        </div>
                    </div>)}

            </div>
        )
    }
}

export default UnPokemon
