import React, { Component } from 'react'
import './UnPokemon.css'

class UnPokemon extends Component {

    state = {
        loading: true,
        pokemon: null,
        ability1: null,
        ability2: null,
    }

    async componentDidMount() {
        const url = 'https://pokeapi.co/api/v2/pokemon/charizard'
        const response = await fetch(url)
        const data = await response.json()
        this.setState({ pokemon: data, loading: false })
        console.log(this.state.pokemon.sprites.back_default)
        const urlAbi1 = this.state.pokemon.abilities[0].ability.url
        const urlAbi2 = this.state.pokemon.abilities[1].ability.url
        const responseAbi1 = await fetch(urlAbi1)
        const responseAbi2 = await fetch(urlAbi2)
        const dataAbi1 = await responseAbi1.json()
        const dataAbi2 = await responseAbi2.json()
    }

    render() {
        return (
            <div>
                {this.state.loading ? (<div>loading...</div>) : (
                    <div Class='page'>
                        <div Class='title'>
                            {this.state.pokemon.name}
                        </div>
                        <div Class='body'>
                            <img Class='image' src={this.state.pokemon.sprites.front_default} />
                            <div>Type : {this.state.pokemon.types[0].type.name}, {this.state.pokemon.types[1].type.name}</div>
                            <div>Abilities :</div>
                            <div>{this.state.pokemon.abilities[0].ability.name} :</div>
                            <div>{this.state.pokemon.abilities[0].ability.url.effect_entries[0].effect}</div>
                            {this.state.pokemon.abilities[1].ability.name}</div>
                    </div>
                )}

            </div>
        )
    }
}

export default UnPokemon
