import React, { Component } from 'react';
import './App.css';
import ListPokemon from './Components/ListPokemon.js';
import PokemonDetails from './Components/PokemonDetails';

function updatePage(nom) {
  this.setState({ nom })
}


class App extends Component {

  constructor(props) {
    super(props)

    this.state = {
      nom: "list"
    }
    updatePage = updatePage.bind(this)
  }


  switchpage() {
    switch (this.state.nom) {
      case "list":
        return <ListPokemon />
        break;
      case "pokemon":
        return <PokemonDetails />
        break;
    }
  }

  render() {
    return (
      <div className="App">
        {this.switchpage()}
      </div>
    );
  }
}

export default App;
