import React, { Component } from 'react'

class TestAPI extends Component {

    state = {
        loading: true,
        pokemon: null
    }

    async componentDidMount() {
        const url = 'https://pokeapi.co/api/v2/pokemon/charizard'
        const response = await fetch(url)
        const data = await response.json()
        this.setState({ pokemon: data, loading: false })
        console.log(this.state.pokemon.sprites)
    }

    render() {
        return (
            <div>
                {this.state.loading ?
                    <div>loading...</div> :
                    <div>{this.state.pokemon.types[0].type.name}</div>}
                <img src={this.state.pokemon.sprites.back_default} />
            </div>
        )
    }
}

export default TestAPI
