import React, { Component } from 'react'
import UnPokemon from './UnPokemon'

class ListPokemon extends Component {

    state = {
        listPoke: null
    }

    async componentDidMount() {
        const url = 'https://pokeapi.co/api/v2/pokemon/'
        const response = await fetch(url)
        const data = await response.json()

        this.setState({ listPoke: data })
        console.log(this.state.listPoke.results[0].name)
    }

    render() {
        return (
            <div>
                {this.state.listPoke.results.map((name, i) => {
                    return (
                        <UnPokemon pokeName={name.name} />
                    )
                })}
            </div>
        )
    }
}

export default ListPokemon
