import React, { Component } from 'react'
import ListPokemon from './ListPokemon.js'
import PokemonDetails from './PokemonDetails.js'
import UnPokemon from './UnPokemon.js'

const Buttons = ({ onToggle }) => (
    <div className="buttons">
        <button className="list" onClick={onToggle}>
            List
        </button>
        <button className="pokemon" onClick={onToggle}>
            Pokemon
        </button>
    </div>
)

const Main = ({ activeSection }) => (
    <React.Fragment>
        <ListPokemon activeSection={activeSection} />
        <PokemonDetails activeSection={activeSection} />
    </React.Fragment>
)

class Page extends Component {

    constructor(props) {
        super(props);
        this.state = {
            activeSection: ""
        };

        this.handleToggleSection = this.handleToggleSection.bind(this);
    }

    handleToggleSection(e) {
        const { name } = e.target;
        this.setState(() => ({
            activeSection: name
        }));
    }

    render() {
        return (
            <div className="App">
                <Buttons onToggle={this.handleToggleSection} />
                <Main activeSection={this.state.activeSection} />
            </div>
        );
    }
}

export default Page
