import React, { Component } from 'react'
import "./myWidget.css"


class MyWidget extends React.Component {
    state = {
        loading: true,
        people: []
    };

    async componentDidMount() {
        const url = "http://localhost:3001/posts";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({ people: data, loading: false });
    }

    render() {
        if (this.state.loading) {
            return <div>loading...</div>;
        }

        if (!this.state.people.length) {
            return <div>didn't get a person</div>;
        }

        return (

            <div>


                <div class="titre">
                    Mon Ã©quipe

                </div>

                {this.state.people.map(person => (
                    <div key={person.prenom}>
                        <div>prenom : {person.prenom}</div>
                        <div>classement{person.classement}</div>
                        <div>nombre poke {person.nombrePoke}</div>
                        <div>points {person.points}</div>
                        <div>medaille1{person.medailles.medaille1}</div>
                        <div>medaille1{person.medailles.medaille2}</div>
                        <div>medaille1{person.medailles.medaille3}</div>
                        <br />


                    </div>
                ))}
            </div>
        );
    }
}
export default MyWidget
