import React, { Component } from 'react'
import ListPokemon from './ListPokemon.js'
import PokemonDetails from './PokemonDetails.js'
import UnPokemon from './UnPokemon.js'

class Page extends Component {

    constructor(props) {
        super(props)

        this.state = {
            nom: "list"
        }
        updatePage = updatePage.bind(this)
    }

    switchpage() {
        switch (this.state.nom) {
            case "list":
                return <ListPokemon />
                break;
            case "pokemon":
                return <PokemonDetails />
                break;
        }
    }

    render() {
        return (
            <div className="App">
                {this.switchpage()}
            </div>
        );
    }
}
